import { useContext, useState, useEffect } from "react";
import { Container, Row, Col, Button, Form, Alert } from "react-bootstrap";
import Swal from "sweetalert2";
import UserContext from "../UserContext";
import { Navigate } from "react-router-dom";

export default function ProfileSettings() {
    const { user, setUser } = useContext(UserContext);

    const [userInfo, setUserInfo] = useState({});
    const [newPassword, setNewPassword] = useState("");
    const [confirmPassword, setConfirmPassword] = useState("");
    const [message, setMessage] = useState("");

    useEffect(() => {
        async function fetchUserData() {
            try {
                const response = await fetch("http://localhost:4000/profile", {
                    method: "GET",
                    headers: {
                        "Content-Type": "application/json",
                        Authorization: `Bearer ${localStorage.getItem("token")}`,
                    },
                });

                const data = await response.json();
                if (response.ok && data.code === "USER-FOUND") {
                    setUserInfo(data.result);
                } else {
                    setMessage("Could not load user data.");
                }
            } catch (error) {
                setMessage("An error occurred while fetching user data.");
                console.error("Error fetching user profile data:", error);
            }
        }

        fetchUserData();
    }, []);

    const handleUpdatePassword = async (e) => {
        e.preventDefault();

        if (!userInfo.email) {
            Swal.fire({
                title: "No user email found!",
                icon: "error",
            });
            return;
        }

        if (newPassword !== confirmPassword) {
            Swal.fire({
                title: "Passwords do not match",
                icon: "error",
            });
            return;
        }

        try {
            const response = await fetch("http://localhost:4000/update-password", {
                method: "POST",
                headers: {
                    "Content-Type": "application/json",
                    Authorization: `Bearer ${localStorage.getItem("token")}`,
                },
                body: JSON.stringify({
                    email: userInfo.email,
                    oldPassword: "",
                    newPassword,
                }),
            });

            const data = await response.json();

            if (response.ok && data.code === "PASSWORD-UPDATED") {
                Swal.fire({
                    title: "Password Updated!",
                    icon: "success",
                });
                setNewPassword("");
                setConfirmPassword("");
            } else {
                Swal.fire({
                    title: "Update Failed",
                    text: data.message || "Unexpected server response.",
                    icon: "error",
                });
            }
        } catch (error) {
            Swal.fire({
                title: "Server Error",
                icon: "error",
            });
            console.error("Error updating password:", error);
        }
    };

    return (
        user.id === null ? (
            <Navigate to="/login" />
        ) : (
            <Container fluid className="vh-100">
                <Row>
                    <Col className="vh-100 bg-light col-6 d-flex flex-column align-items-center justify-content-center text-center">
                        <h1 className="display-5 fw-bold">Welcome to Your Profile!</h1>
                        <p className="display-6">Manage your account and settings here.</p>
                    </Col>

                    <Col className="vh-100 col-6">
                        <Container fluid className="p-5 d-flex flex-column align-items-center justify-content-center vh-100">
                            {message && <Alert variant="danger">{message}</Alert>}

                            <Form
                                className="w-100 p-5 shadow rounded-3 border-bottom border-3 border-success"
                                onSubmit={handleUpdatePassword}
                            >
                                <h1 className="display-5 fw-bold mb-4">Update Password</h1>

                                <Form.Group className="mb-3" controlId="userEmail">
                                    <Form.Label>Email</Form.Label>
                                    <Form.Control
                                        type="email"
                                        value={userInfo.email || ""}
                                        disabled
                                    />
                                </Form.Group>

                                <Form.Group className="mb-3" controlId="newPassword">
                                    <Form.Label>New Password</Form.Label>
                                    <Form.Control
                                        type="password"
                                        placeholder="Enter new password"
                                        value={newPassword}
                                        onChange={(e) => setNewPassword(e.target.value)}
                                        required
                                    />
                                </Form.Group>

                                <Form.Group className="mb-3" controlId="confirmPassword">
                                    <Form.Label>Confirm New Password</Form.Label>
                                    <Form.Control
                                        type="password"
                                        placeholder="Re-enter new password"
                                        value={confirmPassword}
                                        onChange={(e) => setConfirmPassword(e.target.value)}
                                        required
                                    />
                                </Form.Group>

                                <Form.Group className="mb-3">
                                    <Button variant="success" className="w-100 rounded-pill" type="submit">
                                        Update Password
                                    </Button>
                                </Form.Group>
                            </Form>
                        </Container>
                    </Col>
                </Row>
            </Container>
        )
    );
}
