import React, { useState, useEffect } from "react";
import { Button, Form, Container } from "react-bootstrap";

const Profile = () => {
  const [userData, setUserData] = useState({
    name: "",
    email: "",
  });

  const [passwords, setPasswords] = useState({
    oldPassword: "",
    newPassword: "",
  });

  useEffect(() => {
    // Fetch user data from backend
    fetch("http://localhost:4000/users/details", {
      method: "POST",
      headers: {
        Authorization: `Bearer ${localStorage.getItem("token")}`,
      },
    })
      .then((res) => res.json())
      .then((data) => {
        if (data.result) {
          setUserData({
            name: data.result.name,
            email: data.result.email,
          });
        }
      });
  }, []);

  const handlePasswordChange = () => {
    // Send request to backend to update password
    fetch("http://localhost:4000/users/update-password", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${localStorage.getItem("token")}`,
      },
      body: JSON.stringify({
        oldPassword: passwords.oldPassword,
        newPassword: passwords.newPassword,
      }),
    })
      .then((res) => res.json())
      .then((data) => {
        alert(data.message);
      });
  };

  return (
    <Container>
      <h2>User Profile</h2>
      <Form>
        <Form.Group>
          <Form.Label>Name:</Form.Label>
          <Form.Control value={userData.name} readOnly />
        </Form.Group>
        <Form.Group>
          <Form.Label>Email:</Form.Label>
          <Form.Control value={userData.email} readOnly />
        </Form.Group>
        <hr />
        <h4>Change Password</h4>
        <Form.Group>
          <Form.Label>Old Password</Form.Label>
          <Form.Control
            type="password"
            placeholder="Enter your old password"
            onChange={(e) =>
              setPasswords({ ...passwords, oldPassword: e.target.value })
            }
          />
        </Form.Group>
        <Form.Group>
          <Form.Label>New Password</Form.Label>
          <Form.Control
            type="password"
            placeholder="Enter your new password"
            onChange={(e) =>
              setPasswords({ ...passwords, newPassword: e.target.value })
            }
          />
        </Form.Group>
        <Button variant="warning" onClick={handlePasswordChange}>
          Update Password
        </Button>
      </Form>
    </Container>
  );
};

export default Profile;
