import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { Container, Form, Button, Card, Row, Col, Alert } from 'react-bootstrap';

const ProfileSettings = () => {
  const [user, setUser] = useState(null);
  const [oldPassword, setOldPassword] = useState('');
  const [newPassword, setNewPassword] = useState('');
  const [confirmNewPassword, setConfirmNewPassword] = useState('');
  const [message, setMessage] = useState('');
  const navigate = useNavigate();

  useEffect(() => {

    fetch('http://localhost:4000/users/details', {
      method: 'POST',
      headers: {
        Authorization: `Bearer ${localStorage.getItem('token')}`,
      },
    })
      .then(response => response.json())
      .then(data => {
        if (data.code === "USER-FOUND") {
          setUser(data.result);
        }
      })
      .catch(() => {
        setMessage('Failed to fetch user data.');
      });
  }, []);

  const handleSubmit = (e) => {
    e.preventDefault();

    if (newPassword !== confirmNewPassword) {
      setMessage('Passwords do not match.');
      return;
    }

    fetch('http://localhost:4000/users/update-password', {
      method: 'PUT',
      headers: {
        'Content-Type': 'application/json',
        Authorization: `Bearer ${localStorage.getItem('token')}`,
      },
      body: JSON.stringify({ oldPassword, newPassword }),
    })
      .then(response => response.json())
      .then(data => {
        setMessage(data.message);
      })
      .catch(() => {
        setMessage('Error updating password.');
      });
  };

  return (
    <Container fluid className="vh-100 p-4 bg-light">
      {/* Page Title Section */}
      <Container className="mb-4">
        <h1 className="display-3 text-center mb-3 fw-bold">Change Password</h1>
      </Container>

      {/* Main Content */}
      {user ? (
        <Row className="justify-content-center">
          <Col lg={6} md={8} sm={12}>
            <Card className="shadow-sm rounded-3 p-4">
              <Card.Body>
                <h3 className="text-center mb-3">{user.firstName} {user.lastName}</h3>
                <hr />

                {/* Password Form */}
                <Form onSubmit={handleSubmit}>
                  <Form.Group className="mb-3" controlId="oldPassword">
                    <Form.Label>Old Password</Form.Label>
                    <Form.Control
                      type="password"
                      placeholder="Enter your current password"
                      value={oldPassword}
                      onChange={(e) => setOldPassword(e.target.value)}
                    />
                  </Form.Group>

                  <Form.Group className="mb-3" controlId="newPassword">
                    <Form.Label>New Password</Form.Label>
                    <Form.Control
                      type="password"
                      placeholder="Enter your new password"
                      value={newPassword}
                      onChange={(e) => setNewPassword(e.target.value)}
                    />
                  </Form.Group>

                  <Form.Group className="mb-3" controlId="confirmNewPassword">
                    <Form.Label>Confirm New Password</Form.Label>
                    <Form.Control
                      type="password"
                      placeholder="Re-enter your new password"
                      value={confirmNewPassword}
                      onChange={(e) => setConfirmNewPassword(e.target.value)}
                    />
                  </Form.Group>

                  <Button variant="warning" type="submit" className="w-100 mt-2 rounded-pill">
                    Update Password
                  </Button>
                </Form>

                {/* Message Alert Section */}
                {message && (
                  <Alert variant={message.includes('Error') ? 'danger' : 'success'} className="mt-3 text-center">
                    {message}
                  </Alert>
                )}
              </Card.Body>
            </Card>
          </Col>
        </Row>
      ) : (
        <Container className="text-center mt-4">
          <p>Loading...</p>
        </Container>
      )}
    </Container>
  );
};

export default ProfileSettings;
