import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { Container, Row, Col, Form, Button, Card, Alert, Image } from 'react-bootstrap';
import Swal from 'sweetalert2';

const ProfileSettings = () => {
  const [user, setUser] = useState(null);
  const [oldPassword, setOldPassword] = useState('');
  const [newPassword, setNewPassword] = useState('');
  const [confirmNewPassword, setConfirmNewPassword] = useState('');
  const [message, setMessage] = useState('');

  const navigate = useNavigate();

  // Fetch user details
  useEffect(() => {
    fetch('http://localhost:4000/users/details', {
      method: 'POST',
      headers: {
        Authorization: `Bearer ${localStorage.getItem('token')}`,
      },
    })
      .then(response => response.json())
      .then(data => {
        if (data.code === 'USER-FOUND') {
          setUser(data.result);
        }
      })
      .catch(() => {
        setMessage('Unable to load user data');
      });
  }, []);

  const handleSubmit = (e) => {
    e.preventDefault();

    if (newPassword !== confirmNewPassword) {
      setMessage('Passwords must match');
      return;
    }

    fetch('http://localhost:4000/users/update-password', {
      method: 'PUT',
      headers: {
        'Content-Type': 'application/json',
        Authorization: `Bearer ${localStorage.getItem('token')}`,
      },
      body: JSON.stringify({ oldPassword, newPassword }),
    })
      .then(response => response.json())
      .then(data => {
        Swal.fire({
          title: data.message.includes('Error') ? 'Error' : 'Success',
          text: data.message,
          icon: data.message.includes('Error') ? 'error' : 'success',
        });
        setMessage(data.message);
      })
      .catch(() => {
        Swal.fire({
          title: 'Error',
          text: 'Failed to change password',
          icon: 'error',
        });
      });
  };

  return (
    <Container fluid className="vh-100 bg-light p-4">
      {/* Page Title */}
      <Container className="text-center mb-4">
        <h1 className="display-3 fw-bold">Change Your Password</h1>
      </Container>

      {/* Main Card Container */}
      <Row className="justify-content-center">
        <Col lg={6} md={8} sm={12}>
          <Card className="shadow-sm rounded-3 p-4">
            <Card.Body>
              {/* User Info */}
              {user && (
                <div className="text-center mb-4">
                  <Image
                    src={user.profilePicture || 'https://via.placeholder.com/100'}
                    alt="User Image"
                    className="rounded-circle mb-2"
                    style={{ width: '80px', height: '80px' }}
                  />
                  <h4 className="fw-bold">{user.firstName} {user.lastName}</h4>
                  <p className="text-muted">{user.email}</p>
                  <hr />
                </div>
              )}

              {/* Password Change Form */}
              <Form onSubmit={handleSubmit}>
                <Form.Group className="mb-3" controlId="oldPassword">
                  <Form.Label>Current Password</Form.Label>
                  <Form.Control
                    type="password"
                    placeholder="Enter your current password"
                    value={oldPassword}
                    onChange={(e) => setOldPassword(e.target.value)}
                  />
                </Form.Group>

                <Form.Group className="mb-3" controlId="newPassword">
                  <Form.Label>New Password</Form.Label>
                  <Form.Control
                    type="password"
                    placeholder="Enter a new password"
                    value={newPassword}
                    onChange={(e) => setNewPassword(e.target.value)}
                  />
                </Form.Group>

                <Form.Group className="mb-3" controlId="confirmNewPassword">
                  <Form.Label>Confirm New Password</Form.Label>
                  <Form.Control
                    type="password"
                    placeholder="Re-enter your new password"
                    value={confirmNewPassword}
                    onChange={(e) => setConfirmNewPassword(e.target.value)}
                  />
                </Form.Group>

                {/* Submit Button */}
                <Button variant="warning" type="submit" className="w-100 rounded-pill mt-2">
                  Update Password
                </Button>

                {/* Success/Error Alert */}
                {message && (
                  <Alert variant={message.includes('Error') ? 'danger' : 'success'} className="mt-3">
                    {message}
                  </Alert>
                )}
              </Form>
            </Card.Body>
          </Card>
        </Col>
      </Row>
    </Container>
  );
};

export default ProfileSettings;
