import { useState, useEffect } from "react";
import { Container, Form, Button, Alert } from "react-bootstrap";
import Swal from "sweetalert2";

export default function Profile() {
  const [userData, setUserData] = useState({});
  const [oldPassword, setOldPassword] = useState("");
  const [newPassword, setNewPassword] = useState("");
  const [error, setError] = useState("");

  useEffect(() => {
    fetchUserData();
  }, []);

  const fetchUserData = async () => {
    const token = localStorage.getItem("token");

    if (!token) return;

    const response = await fetch("http://localhost:4000/profile", {
      method: "GET",
      headers: {
        Authorization: `Bearer ${token}`,
      },
    });

    const data = await response.json();

    if (data.code === "USER-FOUND") {
      setUserData(data.result);
    } else {
      setError("Unable to fetch user data.");
    }
  };

  const handlePasswordChange = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem("token");

    const response = await fetch("http://localhost:4000/profile/update-password", {
      method: "POST",
      headers: {
        Authorization: `Bearer ${token}`,
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        oldPassword,
        newPassword,
      }),
    });

    const data = await response.json();

    if (data.code === "PASSWORD-UPDATED") {
      Swal.fire({
        title: "Password Changed",
        icon: "success",
      });
      setOldPassword("");
      setNewPassword("");
    } else {
      Swal.fire({
        title: "Error",
        text: data.message,
        icon: "error",
      });
    }
  };

  return (
    <Container>
      <h1 className="mt-3">User Profile</h1>
      {error && <Alert variant="danger">{error}</Alert>}

      <Form>
        <Form.Group className="mb-3">
          <Form.Label>Name</Form.Label>
          <Form.Control type="text" value={userData.name || ""} readOnly />
        </Form.Group>

        <Form.Group className="mb-3">
          <Form.Label>Email</Form.Label>
          <Form.Control type="email" value={userData.email || ""} readOnly />
        </Form.Group>

        <Form.Group className="mb-3">
          <Form.Label>Description</Form.Label>
          <Form.Control type="text" value={userData.description || ""} readOnly />
        </Form.Group>

        <hr />
        <h3>Change Password</h3>
        <Form.Group className="mb-3">
          <Form.Control
            type="password"
            placeholder="Enter old password"
            value={oldPassword}
            onChange={(e) => setOldPassword(e.target.value)}
          />
        </Form.Group>

        <Form.Group className="mb-3">
          <Form.Control
            type="password"
            placeholder="Enter new password"
            value={newPassword}
            onChange={(e) => setNewPassword(e.target.value)}
          />
        </Form.Group>

        <Button variant="warning" className="w-100" onClick={handlePasswordChange}>
          Update Password
        </Button>
      </Form>
    </Container>
  );
}
