import React, { useState, useContext, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { Button, Form, Container, Row, Col } from 'react-bootstrap';
import UserContext from '../UserContext';

export default function Profile() {
  const { user, unsetUser } = useContext(UserContext);
  const navigate = useNavigate();

  const [oldPassword, setOldPassword] = useState('');
  const [newPassword, setNewPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [error, setError] = useState('');

  const handlePasswordChange = () => {
    if (newPassword !== confirmPassword) {
      setError('Passwords do not match');
      return;
    }

    fetch("http://localhost:4000/users/update-password", {
      method: "POST",
      headers: {
        'Content-Type': 'application/json',
        Authorization: `Bearer ${localStorage.getItem('token')}`,
      },
      body: JSON.stringify({
        oldPassword,
        newPassword,
      }),
    })
      .then(res => res.json())
      .then(data => {
        if (data.code === 'PASSWORD-UPDATED') {
          setError('Password updated successfully');
        } else {
          setError(data.message || 'Error');
        }
      })
      .catch(() => setError('Server error occurred.'));
  };

  useEffect(() => {
    if (!user.id) {
      navigate('/login');
    }
  }, [user, navigate]);

  return (
    <Container>
      <h2 className="mt-3 mb-3">Profile Settings</h2>
      <Row>
        <Col md={6}>
          <Form>
            <Form.Group className="mb-3" controlId="oldPassword">
              <Form.Label>Enter Old Password</Form.Label>
              <Form.Control
                type="password"
                placeholder="Enter your old password"
                value={oldPassword}
                onChange={(e) => setOldPassword(e.target.value)}
              />
            </Form.Group>
            <Form.Group className="mb-3" controlId="newPassword">
              <Form.Label>Enter New Password</Form.Label>
              <Form.Control
                type="password"
                placeholder="Enter new password"
                value={newPassword}
                onChange={(e) => setNewPassword(e.target.value)}
              />
            </Form.Group>
            <Form.Group className="mb-3" controlId="confirmPassword">
              <Form.Label>Confirm New Password</Form.Label>
              <Form.Control
                type="password"
                placeholder="Confirm new password"
                value={confirmPassword}
                onChange={(e) => setConfirmPassword(e.target.value)}
              />
            </Form.Group>
            <Button
              variant="warning"
              onClick={handlePasswordChange}
              className="w-100"
            >
              Update Password
            </Button>
          </Form>
          {error && <p className="text-danger mt-2">{error}</p>}
        </Col>
      </Row>
      <div className="text-center mt-4">
        <Button onClick={unsetUser} variant="danger">Logout</Button>
      </div>
    </Container>
  );
}
