import { useState } from "react";
import { Container, Form, Button } from "react-bootstrap";
import Swal from "sweetalert2";

export default function Profile() {
  const [oldPassword, setOldPassword] = useState("");
  const [newPassword, setNewPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");

  const handlePasswordChange = (e) => {
    e.preventDefault(); // Prevent form submission

    if (newPassword !== confirmPassword) {
      Swal.fire({
        title: "Passwords do not match",
        text: "Please make sure both passwords are the same.",
        icon: "error"
      });
      return;
    }

    const token = localStorage.getItem("token");
    const email = localStorage.getItem("email");

    if (!token || !email) {
      Swal.fire({
        title: "Authentication Error",
        text: "Please log in to continue.",
        icon: "error"
      });
      return;
    }

    fetch("http://localhost:4000/users/updatePassword", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${token}`
      },
      body: JSON.stringify({
        email,
        oldPassword,
        newPassword
      })
    })
    .then((response) => response.json())
    .then((data) => {
      if (data.code === "PASSWORD-UPDATED") {
        Swal.fire({
          title: "Password Updated",
          text: data.message,
          icon: "success"
        });
        setOldPassword("");
        setNewPassword("");
        setConfirmPassword("");
      } else {
        Swal.fire({
          title: "Error",
          text: data.message || "Something went wrong!",
          icon: "error"
        });
      }
    })
    .catch((error) => {
      console.error("Error:", error);
      Swal.fire({
        title: "Error",
        text: "Server error occurred.",
        icon: "error"
      });
    });
  };

  return (
    <Container fluid className="vh-100 p-5">
      <h1 className="display-3 mb-5">Change Password</h1>
      <Form onSubmit={handlePasswordChange} className="w-100 p-5 shadow rounded-3 border-warning">
        <Form.Group className="mb-3" controlId="oldPassword">
          <Form.Label>Old Password</Form.Label>
          <Form.Control
            type="password"
            placeholder="Enter your old password"
            value={oldPassword}
            onChange={(e) => setOldPassword(e.target.value)}
            required
          />
        </Form.Group>

        <Form.Group className="mb-3" controlId="newPassword">
          <Form.Label>New Password</Form.Label>
          <Form.Control
            type="password"
            placeholder="Enter your new password"
            value={newPassword}
            onChange={(e) => setNewPassword(e.target.value)}
            required
          />
        </Form.Group>

        <Form.Group className="mb-3" controlId="confirmPassword">
          <Form.Label>Confirm New Password</Form.Label>
          <Form.Control
            type="password"
            placeholder="Confirm your new password"
            value={confirmPassword}
            onChange={(e) => setConfirmPassword(e.target.value)}
            required
          />
        </Form.Group>

        <Button variant="warning" className="w-100 rounded-pill" type="submit">
          Update Password
        </Button>
      </Form>
    </Container>
  );
}
