import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { Button, Form, Card, Col, Row, Alert } from 'react-bootstrap';

// API helper function for GET request
const fetchUserDetails = async (token) => {
  try {
    const response = await fetch('/users/details', {
      method: 'POST',
      headers: {
        Authorization: `Bearer ${token}`,
      },
    });

    const data = await response.json();
    if (response.ok && data.code === 'USER-FOUND') {
      return { success: true, data: data.result };
    }
    return { success: false, message: data.message || 'Failed to fetch user data' };
  } catch (error) {
    console.error('Error fetching user details', error);
    return { success: false, message: 'Network or server issue occurred' };
  }
};

// API helper function for password update
const updateUserPassword = async (token, oldPassword, newPassword, confirmNewPassword) => {
  try {
    const response = await fetch('/users/update-password', {
      method: 'PUT',
      headers: {
        'Content-Type': 'application/json',
        Authorization: `Bearer ${token}`,
      },
      body: JSON.stringify({ oldPassword, newPassword, confirmNewPassword }),
    });

    const data = await response.json();
    if (response.ok && data.code === 'PASSWORD-UPDATED') {
      return { success: true, message: data.message };
    }
    return { success: false, message: data.message || 'Failed to update password' };
  } catch (error) {
    console.error('Error updating password', error);
    return { success: false, message: 'Network or server issue occurred' };
  }
};

const ProfileSettings = () => {
  const [user, setUser] = useState(null);
  const [oldPassword, setOldPassword] = useState('');
  const [newPassword, setNewPassword] = useState('');
  const [confirmNewPassword, setConfirmNewPassword] = useState('');
  const [message, setMessage] = useState('');
  const navigate = useNavigate();

  // Fetch user details on component mount
  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      setMessage('Authentication token is missing. Please log in again.');
      navigate('/login');
      return;
    }

    const getUserDetails = async () => {
      const result = await fetchUserDetails(token);
      if (result.success) {
        setUser(result.data);
      } else {
        setMessage(result.message);
      }
    };

    getUserDetails();
  }, [navigate]);

  // Handle password change form submission
  const handlePasswordChange = async (e) => {
    e.preventDefault();
    if (newPassword !== confirmNewPassword) {
      setMessage('Passwords do not match.');
      return;
    }

    const token = localStorage.getItem('token');
    if (!token) {
      setMessage('Authentication token is missing. Please log in again.');
      return;
    }

    const response = await updateUserPassword(token, oldPassword, newPassword, confirmNewPassword);

    setMessage(response.message);
  };

  return (
    <div className="profile-settings container mt-5">
      <Card className="shadow-sm p-4">
        <Card.Body>
          <h2 className="text-center mb-4">Profile Settings</h2>

          {user ? (
            <>
              <h3 className="text-center mb-4">Welcome, {user.firstName} {user.lastName}</h3>
              <Row>
                <Col md={6} className="mx-auto">
                  <Form onSubmit={handlePasswordChange}>
                    {/* Old Password Input */}
                    <Form.Group controlId="oldPassword" className="mb-3">
                      <Form.Label>Old Password</Form.Label>
                      <Form.Control
                        type="password"
                        placeholder="Enter old password"
                        value={oldPassword}
                        onChange={(e) => setOldPassword(e.target.value)}
                      />
                    </Form.Group>

                    {/* New Password Input */}
                    <Form.Group controlId="newPassword" className="mb-3">
                      <Form.Label>New Password</Form.Label>
                      <Form.Control
                        type="password"
                        placeholder="Enter new password"
                        value={newPassword}
                        onChange={(e) => setNewPassword(e.target.value)}
                      />
                    </Form.Group>

                    {/* Confirm New Password Input */}
                    <Form.Group controlId="confirmNewPassword" className="mb-3">
                      <Form.Label>Confirm New Password</Form.Label>
                      <Form.Control
                        type="password"
                        placeholder="Confirm new password"
                        value={confirmNewPassword}
                        onChange={(e) => setConfirmNewPassword(e.target.value)}
                      />
                    </Form.Group>

                    {/* Submit Button */}
                    <Button variant="primary" type="submit" className="w-100">
                      Update Password
                    </Button>
                  </Form>

                  {/* Message Display */}
                  {message && (
                    <Alert variant={message.includes('Error') ? 'danger' : 'success'} className="mt-3">
                      {message}
                    </Alert>
                  )}
                </Col>
              </Row>
            </>
          ) : (
            <div className="text-center">
              <p>Loading user data...</p>
            </div>
          )}
        </Card.Body>
      </Card>
    </div>
  );
};

export default ProfileSettings;
