import React, { useState, useEffect } from "react";
import { Container, Form, Button } from "react-bootstrap";

const Profile = () => {
  const [userInfo, setUserInfo] = useState({
    name: "",
    email: "",
  });

  const [passwords, setPasswords] = useState({
    newPassword: "",
    confirmPassword: "",
  });

  const [message, setMessage] = useState("");

  useEffect(() => {
    // Fetch user information when the profile page loads
    fetch("http://localhost:4000/users/details", {
      method: "POST",
      headers: {
        Authorization: `Bearer ${localStorage.getItem("token")}`,
      },
    })
      .then((response) => response.json())
      .then((data) => {
        if (data.result) {
          setUserInfo({
            name: data.result.name,
            email: data.result.email,
          });
        }
      })
      .catch((error) => console.error("Error fetching user info:", error));
  }, []);

  const handlePasswordChange = () => {
    if (passwords.newPassword !== passwords.confirmPassword) {
      setMessage("Passwords do not match.");
      return;
    }

    fetch("http://localhost:4000/users/update-password", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${localStorage.getItem("token")}`,
      },
      body: JSON.stringify({
        newPassword: passwords.newPassword,
      }),
    })
      .then((response) => response.json())
      .then((data) => {
        setMessage(data.message);
      })
      .catch(() => setMessage("Error updating password."));
  };

  return (
    <Container>
      <h2>User Profile</h2>
      <Form>

        <Form.Group>
          <Form.Label>Email:</Form.Label>
          <Form.Control type="text" value={userInfo.email} readOnly />
        </Form.Group>
        <hr />
        <h4>Change Password</h4>
        <Form.Group>
          <Form.Label>New Password</Form.Label>
          <Form.Control
            type="password"
            placeholder="Enter new password"
            onChange={(e) =>
              setPasswords({ ...passwords, newPassword: e.target.value })
            }
          />
        </Form.Group>
        <Form.Group>
          <Form.Label>Confirm New Password</Form.Label>
          <Form.Control
            type="password"
            placeholder="Confirm new password"
            onChange={(e) =>
              setPasswords({ ...passwords, confirmPassword: e.target.value })
            }
          />
        </Form.Group>
        {message && (
          <p style={{ color: "red", marginTop: "10px" }}>{message}</p>
        )}
        <Button variant="warning" className="mt-3" onClick={handlePasswordChange}>
          Update Password
        </Button>
      </Form>
    </Container>
  );
};

export default Profile;
