import { useState, useEffect } from "react";
import { Container, Form, Button, Alert } from "react-bootstrap";
import Swal from "sweetalert2";

export default function ProfileSettings() {
  const [userInfo, setUserInfo] = useState({});
  const [newPassword, setNewPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [message, setMessage] = useState("");

  // Fetch user profile data
  useEffect(() => {
    async function fetchUserData() {
      try {
        const response = await fetch("http://localhost:4000/profile", {
          method: "GET",
          headers: { "Content-Type": "application/json" },
        });

        const data = await response.json();
        if (response.ok && data.code === "USER-FOUND") {
          setUserInfo(data.result);
        } else {
          setMessage("Could not load user data.");
        }
      } catch (error) {
        setMessage("An error occurred while fetching user data.");
        console.error("Error fetching user data:", error);
      }
    }

    fetchUserData();
  }, []);

  // Handle password update
  function handleUpdatePassword(e) {
    e.preventDefault();

    if (!userInfo.email) {
      Swal.fire({
        title: "No user email found!",
        icon: "error",
      });
      return;
    }

    if (newPassword !== confirmPassword) {
      Swal.fire({
        title: "Passwords do not match",
        icon: "error",
      });
      return;
    }

    fetch("http://localhost:4000/update-password", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        email: userInfo.email,
        oldPassword: "",
        newPassword,
      }),
    })
      .then((result) => result.json())
      .then((data) => {
        if (data.code === "PASSWORD-UPDATED") {
          Swal.fire({
            title: "Password Updated!",
            icon: "success",
          });
          setNewPassword("");
          setConfirmPassword("");
        } else {
          Swal.fire({
            title: "Update Failed",
            text: data.message || "Unexpected server response.",
            icon: "error",
          });
        }
      })
      .catch((error) => {
        Swal.fire({
          title: "Server Error",
          icon: "error",
        });
        console.error("Error updating password:", error);
      });
  }

  return (
    <Container fluid className="vh-100 p-5">
      {/* Header Section */}
      <Container className="mb-4">
        <h1 className="display-3 fw-bold">PROFILE SETTINGS</h1>
        {message && <Alert variant="danger">{message}</Alert>}
      </Container>

      {/* Form Section */}
      <Container className="d-flex flex-column col-lg-6 col-12">
        <Form
          className="w-100 p-5 shadow rounded-3 border-bottom border-3 border-info"
          onSubmit={(e) => handleUpdatePassword(e)}
        >
          {/* Display user email */}
          <Form.Group className="mb-3" controlId="userEmail">
            <Form.Label>Email Address</Form.Label>
            <Form.Control type="email" value={userInfo.email || ""} disabled />
          </Form.Group>

          {/* New Password Section */}
          <Form.Group className="mb-3" controlId="newPassword">
            <Form.Label>New Password</Form.Label>
            <Form.Control
              type="password"
              placeholder="Enter new password"
              value={newPassword}
              onChange={(e) => setNewPassword(e.target.value)}
              required
            />
          </Form.Group>

          {/* Confirm Password Section */}
          <Form.Group className="mb-3" controlId="confirmPassword">
            <Form.Label>Confirm New Password</Form.Label>
            <Form.Control
              type="password"
              placeholder="Re-enter new password"
              value={confirmPassword}
              onChange={(e) => setConfirmPassword(e.target.value)}
              required
            />
          </Form.Group>

          {/* Submit Button */}
          <Form.Group className="mb-3">
            <Button variant="info" className="w-100 rounded-pill" type="submit">
              Update Password
            </Button>
          </Form.Group>
        </Form>
      </Container>
    </Container>
  );
}
