import React, { useState, useEffect } from "react";
import { Container, Form, Button } from "react-bootstrap";

const Profile = () => {
  const [oldPassword, setOldPassword] = useState("");
  const [newPassword, setNewPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [message, setMessage] = useState("");

  useEffect(() => {
    // Ensure user is logged in, or redirect if session expired
    if (!localStorage.getItem("token")) {
      window.location.href = "/login";
    }
  }, []);

  const handlePasswordChange = async () => {
    if (newPassword !== confirmPassword) {
      setMessage("Passwords do not match.");
      return;
    }

    const token = localStorage.getItem('token');
    const email = localStorage.getItem('email'); // Fetch user's email from localStorage.

    if (!token || !email) {
      setMessage("Please log in to continue.");
      return;
    }

    try {
      const response = await fetch("http://localhost:4000/users/update-password", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({
          email,
          oldPassword,
          newPassword,
        }),
      });

      const result = await response.json();

      if (result.code === "PASSWORD-UPDATED") {
        setMessage("Password updated successfully.");
      } else {
        setMessage(result.message);
      }
    } catch (error) {
      console.error(error);
      setMessage("Server error occurred.");
    }
  };

  return (
    <Container>
      <h2 className="mt-3 mb-3">Change Password</h2>
      <Form>
        <Form.Group className="mb-3" controlId="oldPassword">
          <Form.Label>Old Password</Form.Label>
          <Form.Control
            type="password"
            placeholder="Enter your old password"
            value={oldPassword}
            onChange={(e) => setOldPassword(e.target.value)}
          />
        </Form.Group>
        <Form.Group className="mb-3" controlId="newPassword">
          <Form.Label>New Password</Form.Label>
          <Form.Control
            type="password"
            placeholder="Enter new password"
            value={newPassword}
            onChange={(e) => setNewPassword(e.target.value)}
          />
        </Form.Group>
        <Form.Group className="mb-3" controlId="confirmPassword">
          <Form.Label>Confirm New Password</Form.Label>
          <Form.Control
            type="password"
            placeholder="Confirm new password"
            value={confirmPassword}
            onChange={(e) => setConfirmPassword(e.target.value)}
          />
        </Form.Group>
        {message && <p style={{ color: "red", marginTop: "10px" }}>{message}</p>}
        <Button
          variant="warning"
          className="mt-3"
          onClick={handlePasswordChange}
        >
          Update Password
        </Button>
      </Form>
    </Container>
  );
};

export default Profile;
