import { useState, useEffect, useContext } from "react";
import { Container, Form, Button } from "react-bootstrap";
import Swal from "sweetalert2";
import { Navigate } from "react-router-dom";
import UserContext from "../UserContext";

export default function Profile() {
  const { user, setUser } = useContext(UserContext); // Using UserContext to track user state

  const [oldPassword, setOldPassword] = useState("");
  const [newPassword, setNewPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");

  // Redirect user if not logged in
  useEffect(() => {
    const token = localStorage.getItem("token");
    if (!token) {
      Swal.fire({
        title: "Not Logged In",
        text: "Please login to continue.",
        icon: "error",
      });
    }
  }, []);

  const handlePasswordChange = (e) => {
    e.preventDefault();

    if (newPassword !== confirmPassword) {
      Swal.fire({
        title: "Passwords do not match",
        text: "Please make sure both passwords are the same.",
        icon: "error",
      });
      return;
    }

    const token = localStorage.getItem("token");

    if (!token) {
      Swal.fire({
        title: "Authentication Error",
        text: "Please log in to continue.",
        icon: "error",
      });
      return;
    }

    fetch("http://localhost:4000/users/updatePassword", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${token}`,
      },
      body: JSON.stringify({
        oldPassword,
        newPassword,
      }),
    })
      .then((response) => response.json())
      .then((data) => {
        if (data.code === "PASSWORD-UPDATED") {
          Swal.fire({
            title: "Password Updated",
            text: data.message,
            icon: "success",
          });
          setOldPassword("");
          setNewPassword("");
          setConfirmPassword("");
        } else {
          Swal.fire({
            title: "Error",
            text: data.message || "Could not change password.",
            icon: "error",
          });
        }
      })
      .catch((error) => {
        Swal.fire({
          title: "Server Error",
          text: "An unexpected server error occurred.",
          icon: "error",
        });
      });
  };

  return !localStorage.getItem("token") ? (
    <Navigate to="/login" />
  ) : (
    <Container fluid className="vh-100 p-5">
      <h1 className="display-3 mb-5">Change Your Password</h1>
      <Form onSubmit={handlePasswordChange} className="w-100 p-5 shadow rounded-3 border-warning">
        <Form.Group className="mb-3" controlId="oldPassword">
          <Form.Label>Old Password</Form.Label>
          <Form.Control
            type="password"
            placeholder="Enter your old password"
            value={oldPassword}
            onChange={(e) => setOldPassword(e.target.value)}
            required
          />
        </Form.Group>

        <Form.Group className="mb-3" controlId="newPassword">
          <Form.Label>New Password</Form.Label>
          <Form.Control
            type="password"
            placeholder="Enter your new password"
            value={newPassword}
            onChange={(e) => setNewPassword(e.target.value)}
            required
          />
        </Form.Group>

        <Form.Group className="mb-3" controlId="confirmPassword">
          <Form.Label>Confirm New Password</Form.Label>
          <Form.Control
            type="password"
            placeholder="Confirm your new password"
            value={confirmPassword}
            onChange={(e) => setConfirmPassword(e.target.value)}
            required
          />
        </Form.Group>

        <Button variant="warning" className="w-100 rounded-pill" type="submit">
          Update Password
        </Button>
      </Form>
    </Container>
  );
}
