import { useState } from "react";
import { Container, Form, Button } from "react-bootstrap";
import Swal from "sweetalert2";

export default function Profile() {
  const [oldPassword, setOldPassword] = useState("");
  const [newPassword, setNewPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");

  const handlePasswordChange = async (e) => {
    e.preventDefault();

    if (newPassword !== confirmPassword) {
      Swal.fire({
        title: "Passwords do not match",
        text: "Please ensure both passwords match.",
        icon: "error"
      });
      return;
    }

    const token = localStorage.getItem("token");
    if (!token) {
      Swal.fire({
        title: "Authentication Required",
        text: "Please log in first.",
        icon: "error"
      });
      return;
    }

    try {
      const response = await fetch("http://localhost:4000/users/updatePassword", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({
          oldPassword,
          newPassword,
        })
      });

      const result = await response.json();
      if (response.ok && result.code === "PASSWORD-UPDATED") {
        Swal.fire({
          title: "Password Updated!",
          text: result.message,
          icon: "success"
        });
        setOldPassword("");
        setNewPassword("");
        setConfirmPassword("");
      } else {
        Swal.fire({
          title: "Error",
          text: result.message || "Unable to update password.",
          icon: "error"
        });
      }
    } catch (error) {
      console.error("Error communicating with server:", error);
      Swal.fire({
        title: "Server Error",
        text: "Unable to process request at this time.",
        icon: "error"
      });
    }
  };

  return (
    <Container fluid className="vh-100 p-5">
      <h1 className="display-3 mb-5">Change Password</h1>
      <Form onSubmit={handlePasswordChange} className="w-100 p-5 shadow rounded-3 border-warning">
        <Form.Group className="mb-3" controlId="oldPassword">
          <Form.Label>Old Password</Form.Label>
          <Form.Control
            type="password"
            placeholder="Enter your old password"
            value={oldPassword}
            onChange={(e) => setOldPassword(e.target.value)}
            required
          />
        </Form.Group>

        <Form.Group className="mb-3" controlId="newPassword">
          <Form.Label>New Password</Form.Label>
          <Form.Control
            type="password"
            placeholder="Enter your new password"
            value={newPassword}
            onChange={(e) => setNewPassword(e.target.value)}
            required
          />
        </Form.Group>

        <Form.Group className="mb-3" controlId="confirmPassword">
          <Form.Label>Confirm New Password</Form.Label>
          <Form.Control
            type="password"
            placeholder="Confirm your new password"
            value={confirmPassword}
            onChange={(e) => setConfirmPassword(e.target.value)}
            required
          />
        </Form.Group>

        <Button variant="warning" className="w-100 rounded-pill" type="submit">
          Update Password
        </Button>
      </Form>
    </Container>
  );
}
