import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { Button, Form, Card, Col, Row, Alert } from 'react-bootstrap';

const Profile = () => {
  const [user, setUser] = useState(null);
  const [oldPassword, setOldPassword] = useState('');
  const [newPassword, setNewPassword] = useState('');
  const [confirmNewPassword, setConfirmNewPassword] = useState('');
  const [message, setMessage] = useState('');
  const navigate = useNavigate();

  // Fetch user details on load
  useEffect(() => {
    fetch('http://localhost:4000/users/details', {
      method: 'POST',
      headers: {
        Authorization: `Bearer ${localStorage.getItem('token')}`,
      },
    })
      .then(response => response.json())
      .then(data => {
        if (data.code === 'USER-FOUND') {
          setUser(data.result);
        } else {
          setMessage('Unable to fetch user data.');
        }
      })
      .catch(error => {
        setMessage('Failed to connect to server.');
      });
  }, []);

  const handleSubmit = (e) => {
    e.preventDefault();

    if (newPassword !== confirmNewPassword) {
      setMessage('Passwords do not match.');
      return;
    }

    fetch('http://localhost:4000/users/updatePassword', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        Authorization: `Bearer ${localStorage.getItem('token')}`,
      },
      body: JSON.stringify({
        oldPassword,
        newPassword,
      }),
    })
      .then(response => response.json())
      .then(data => {
        if (data.code === 'PASSWORD-UPDATED') {
          setMessage('Password updated successfully!');
          setOldPassword('');
          setNewPassword('');
          setConfirmNewPassword('');
        } else {
          setMessage(data.message || 'Error updating password.');
        }
      })
      .catch(error => {
        console.error('Error:', error);
        setMessage('Failed to process request.');
      });
  };

  return (
    <div className="profile-settings container mt-5">
      <Card className="shadow-sm p-4">
        <Card.Body>
          <h2 className="text-center mb-4">Change Password</h2>
          
          {user ? (
            <div>
              <h4 className="text-center mb-3">Welcome, {user.firstName} {user.lastName}</h4>
              <Row>
                <Col md={6} className="mx-auto">
                  <Form onSubmit={handleSubmit}>
                    <Form.Group controlId="oldPassword" className="mb-3">
                      <Form.Label>Old Password</Form.Label>
                      <Form.Control
                        type="password"
                        placeholder="Enter your old password"
                        value={oldPassword}
                        onChange={(e) => setOldPassword(e.target.value)}
                        required
                      />
                    </Form.Group>

                    <Form.Group controlId="newPassword" className="mb-3">
                      <Form.Label>New Password</Form.Label>
                      <Form.Control
                        type="password"
                        placeholder="Enter a new password"
                        value={newPassword}
                        onChange={(e) => setNewPassword(e.target.value)}
                        required
                      />
                    </Form.Group>

                    <Form.Group controlId="confirmNewPassword" className="mb-3">
                      <Form.Label>Confirm New Password</Form.Label>
                      <Form.Control
                        type="password"
                        placeholder="Re-enter new password"
                        value={confirmNewPassword}
                        onChange={(e) => setConfirmNewPassword(e.target.value)}
                        required
                      />
                    </Form.Group>

                    <Button variant="warning" className="w-100 rounded-pill" type="submit">
                      Update Password
                    </Button>
                  </Form>

                  {message && (
                    <Alert variant={message.includes('Error') ? 'danger' : 'success'} className="mt-3">
                      {message}
                    </Alert>
                  )}
                </Col>
              </Row>
            </div>
          ) : (
            <div className="text-center">
              <p>Loading user information...</p>
            </div>
          )}
        </Card.Body>
      </Card>
    </div>
  );
};

export default Profile;
