import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { Container, Form, Button, Card, Image, Alert } from 'react-bootstrap';
import Swal from 'sweetalert2';

const ProfileSettings = () => {
  const [user, setUser] = useState(null);
  const [oldPassword, setOldPassword] = useState('');
  const [newPassword, setNewPassword] = useState('');
  const [confirmNewPassword, setConfirmNewPassword] = useState('');
  const [message, setMessage] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    // Fetch the user details
    fetch('http://localhost:4000/users/details', {
      method: 'POST',
      headers: {
        Authorization: `Bearer ${localStorage.getItem('token')}`,
      },
    })
      .then(response => response.json())
      .then(data => {
        if (data.code === "USER-FOUND") {
          setUser(data.result);
        }
      })
      .catch(error => {
        setMessage('Failed to fetch user data.');
      });
  }, []);

  const handleSubmit = (e) => {
    e.preventDefault();

    if (newPassword !== confirmNewPassword) {
      setMessage('Passwords do not match.');
      return;
    }

    fetch('http://localhost:4000/users/update-password', {
      method: 'PUT',
      headers: {
        'Content-Type': 'application/json',
        Authorization: `Bearer ${localStorage.getItem('token')}`,
      },
      body: JSON.stringify({ oldPassword, newPassword }),
    })
      .then(response => response.json())
      .then(data => {
        setMessage(data.message);
        Swal.fire({
          title: data.message.includes('Error') ? 'Error' : 'Success',
          text: data.message,
          icon: data.message.includes('Error') ? 'error' : 'success',
        });
      })
      .catch(error => {
        setMessage('Error updating password.');
      });
  };

  return (
    <Container fluid className="vh-100 p-4 bg-light">
      <Container className="mb-4">
        <h1 className="display-4 text-center mb-3 fw-bold">Change Password</h1>
      </Container>

      {user ? (
        <Container className="d-flex justify-content-center">
          <Card className="shadow-sm p-4 w-100" style={{ maxWidth: '500px' }}>
            <Card.Body>
              <h3 className="text-center mb-4">{user.firstName} {user.lastName}</h3>
              <hr />
              <Form onSubmit={handleSubmit}>
                <Form.Group className="mb-3" controlId="oldPassword">
                  <Form.Label>Old Password</Form.Label>
                  <Form.Control
                    type="password"
                    placeholder="Enter your current password"
                    value={oldPassword}
                    onChange={(e) => setOldPassword(e.target.value)}
                  />
                </Form.Group>

                <Form.Group className="mb-3" controlId="newPassword">
                  <Form.Label>New Password</Form.Label>
                  <Form.Control
                    type="password"
                    placeholder="Enter your new password"
                    value={newPassword}
                    onChange={(e) => setNewPassword(e.target.value)}
                  />
                </Form.Group>

                <Form.Group className="mb-3" controlId="confirmNewPassword">
                  <Form.Label>Confirm New Password</Form.Label>
                  <Form.Control
                    type="password"
                    placeholder="Re-enter your new password"
                    value={confirmNewPassword}
                    onChange={(e) => setConfirmNewPassword(e.target.value)}
                  />
                </Form.Group>

                <Button variant="primary" className="w-100 rounded-pill" type="submit">
                  Update Password
                </Button>

                {message && (
                  <Alert variant={message.includes('Error') ? 'danger' : 'success'} className="mt-3">
                    {message}
                  </Alert>
                )}
              </Form>
            </Card.Body>
          </Card>
        </Container>
      ) : (
        <Container className="text-center mt-4">
          <p>Loading...</p>
        </Container>
      )}
    </Container>
  );
};

export default ProfileSettings;
